use My_database;

CREATE TABLE Category 
  (Category_id numeric(1,0),
  Name VARCHAR(45),
  PRIMARY KEY (Category_id));
  
CREATE TABLE Products 
  (Barcode numeric(13,0),
  Category_id numeric(1,0) NOT NULL,
  Name VARCHAR(45),
  Price numeric(4,2) check(Price > 0),
  Brand_name BIT,
  PRIMARY KEY (Barcode),
  FOREIGN KEY (Category_id) REFERENCES Category(Category_id));
  
CREATE TABLE Stores 
  (Store_id numeric(2,0),
  Opening_hours VARCHAR(45),
  Size numeric(3,0),
  Street VARCHAR(45),
  Number numeric(3,0),
  Postal_code numeric(5,0),
  City VARCHAR(45),
  PRIMARY KEY (Store_id));
  
  CREATE TABLE Customer 
 ( Card_number numeric(5,0),
  Points INT,
  Name VARCHAR(45) NOT NULL,
  Phone numeric(10,0),
  Pet BIT,
  Family_members INT,
  City VARCHAR(45),
  Postal_code numeric(5,0),
  Street VARCHAR(45),
  Number numeric(3,0),
  Date_of_birth DATE NOT NULL,
  PRIMARY KEY (Card_number));

CREATE TABLE Phone_store 
  (Store_id numeric(2,0),
  Phone numeric(10,0),
  PRIMARY KEY (Phone, Store_id),
  FOREIGN KEY (Store_id) REFERENCES Stores(Store_id));
  
  CREATE TABLE Older_Prices 
  (Start_date DATE,
  End_date DATE,
  Barcode numeric(13,0),
  Price numeric(4,2) check(Price > 0),
  PRIMARY KEY (Start_date,Barcode),
  FOREIGN KEY (Barcode) REFERENCES Products(Barcode)
  on delete cascade);
  
  CREATE TABLE Transactions 
  (Card_number numeric(5,0),
  Payment_method varchar(4),
  Total_amount numeric(5,2),
  Date_time DATETIME ,
  PRIMARY KEY (Date_time,Card_number),
  FOREIGN KEY (Card_number) REFERENCES Customer(Card_number));
  
  
  CREATE TABLE Offers 
  (Store_id numeric(2,0),
  Barcode numeric(13,0),
  Alley numeric(2,0),
  Shelf numeric(2,0),
  PRIMARY KEY (Store_id, Barcode),
  FOREIGN KEY (Barcode) REFERENCES Products(Barcode)
  on delete cascade,
  FOREIGN KEY (Store_id) REFERENCES Stores(Store_id));
  
CREATE TABLE Contains
  (Pieces INT NOT NULL,
  Barcode numeric(13,0),
  Date_time DATETIME,
  Card_number numeric(5,0),
  PRIMARY KEY (Date_time, Barcode, Card_number),
  FOREIGN KEY (Barcode) REFERENCES Products(Barcode)
  on delete cascade,
  FOREIGN KEY (Card_number) REFERENCES Customer(Card_number),
  FOREIGN KEY (Date_time) REFERENCES Transactions(Date_time));
  
