/*the last week of the inserted transactions is the 22nd week of the year (1st - 7th of June)*/
select sum(amount_each_week)/count(Week) as avg_amount_per_week
from (select week(Date_time) as Week, year(Date_time) as Year, sum(Total_amount) as amount_each_week
	from Transactions 
	where Card_number like '124__' and week(Date_time) = 22 and year(Date_time) = 2020 
	group by Week,Year)
    as avg_per_week;
    
select sum(amount_each_month)/count(Month) as avg_amount_per_month
from (select month(Date_time) as Month, year(Date_time) as Year, sum(Total_amount) as amount_each_month
	from Transactions 
	where Card_number like '124__' and month(Date_time) = 06 and year(Date_time) = 2020 
	group by Month,Year)
    as avg_per_month;
  

#Q8
SELECT bar1, bar2, count(*) as cnt
from (select  C1.Barcode as bar1, C2.Barcode as bar2
    from Contains as C1, Contains as C2
    where C1.Barcode < C2.Barcode  and C1.Card_number = C2.Card_number and C1.Date_time = C2.Date_time)
    as den
    group by bar1, bar2
    order by cnt desc
    limit 10 ;
    
